import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import welcomeStyle from './Welcome.module.css';
import { useNavigate } from 'react-router-dom';

const Welcome = ({ user, setUserState }) => {
  const [books, setBooks] = useState([]);
  const [subscriptions, setSubscriptions] = useState([]);
  const [search, setSearch] = useState('');
  const [loading, setLoading] = useState(true);
  const [lastKey, setLastKey] = useState(null);
  const [showingSubscriptions, setShowingSubscriptions] = useState(false);
  const navigate = useNavigate();
  const limit = 12;

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      setLoading(false);
      navigate('/login', { replace: true });
    } else {
      const storedUser = localStorage.getItem('user');
      if (storedUser) {
        setUserState(JSON.parse(storedUser));
      }
      setLoading(false);
    }
  }, [navigate, setUserState]);

  const loadMusic = useCallback((lastEvaluatedKey = null) => {
    const token = localStorage.getItem('token');
    if (token) {
      let url = `http://127.0.0.1:5000/music?limit=${limit}`;
      if (lastEvaluatedKey) {
        url += `&last_evaluated_key=${encodeURIComponent(JSON.stringify(lastEvaluatedKey))}`;
      }
      axios.get(url, {
        headers: { Authorization: `Bearer ${token}` }
      })
      .then(res => {
        const newItems = res.data.items || (Array.isArray(res.data) ? res.data : []);
        setBooks(lastEvaluatedKey ? prev => [...prev, ...newItems] : newItems);
        setLastKey(res.data.lastEvaluatedKey);
      })
      .catch(() => navigate('/login', { replace: true }));
    } else {
      navigate('/login', { replace: true });
    }
  }, [navigate]);

  const loadSubscriptions = () => {
    const token = localStorage.getItem('token');
    axios.get('http://127.0.0.1:5000/subscriptions', {
      headers: { Authorization: `Bearer ${token}` }
    })
    .then(res => setSubscriptions(res.data))
    .catch(err => console.error(err));
  };

  useEffect(() => {
    loadMusic();
  }, [loadMusic]);

  const searchHandler = (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    axios.get(`http://127.0.0.1:5000/music/query?title=${encodeURIComponent(search)}&limit=${limit}`, {
      headers: { Authorization: `Bearer ${token}` }
    })
    .then(res => {
      const newItems = res.data.items || (Array.isArray(res.data) ? res.data : []);
      setBooks(newItems);
      setLastKey(res.data.lastEvaluatedKey);
    })
    .catch(err => console.error(err));
  };

  const handleSubscribe = (song) => {
    const token = localStorage.getItem('token');
    axios.post('http://127.0.0.1:5000/subscribe', song, {
      headers: { Authorization: `Bearer ${token}` }
    })
    .then(() => alert(`${song.title} has been subscribed`))
    .catch(err => {
      console.error('Subscribe failed:', err);
      alert('Error subscribing song.');
    });
  };

  const handleUnsubscribe = (title) => {
    const token = localStorage.getItem('token');
    axios.delete('http://127.0.0.1:5000/subscriptions', {
      headers: { Authorization: `Bearer ${token}` },
      data: { title }
    })
    .then(() => setSubscriptions(prev => prev.filter(song => song.title !== title)))
    .catch(err => console.error(err));
  };

  const logout = () => {
    setUserState(null);
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/login', { replace: true });
  };

  const handleToggleView = () => {
    setShowingSubscriptions(prev => !prev);
    if (!showingSubscriptions) loadSubscriptions();
  };

  const itemsToShow = showingSubscriptions ? subscriptions : books;

  return (
    <div className={welcomeStyle.container}>
      <aside className={welcomeStyle.sidebar}>
        <div className={welcomeStyle.sidebarHeader}>
          <h2>Library</h2>
          <div className={welcomeStyle.userInfo}>
            <div className={welcomeStyle.userAvatar}></div>
            <div>
              <p style={{ color: "white" }}>{user?.fname} {user?.lname}</p>
              <span>Online</span>
            </div>
          </div>
        </div>
        <nav className={welcomeStyle.nav}>
          <button className={welcomeStyle.navItem} onClick={() => setShowingSubscriptions(false)}>Books</button>
          <button className={welcomeStyle.navItem} onClick={handleToggleView}>Subscriptions</button>
          <button className={welcomeStyle.navItem} onClick={logout}>Logout</button>
        </nav>
      </aside>

      <main className={welcomeStyle.mainContent}>
        <div className={welcomeStyle.searchSection}>
          <form onSubmit={searchHandler} className={welcomeStyle.searchForm}>
            <div className={welcomeStyle.searchContainer}>
              <i className="fas fa-search" style={{ margin: '0 5px', color: '#777' }}></i>
              <input
                type="text"
                value={search}
                onChange={(e) => setSearch(e.target.value)}
                placeholder="Search by title"
                className={welcomeStyle.searchInput}
              />
              <button type="submit" className={welcomeStyle.searchButton}>Search</button>
            </div>
          </form>
        </div>

        <section className={welcomeStyle.booksSection}>
          <div className={welcomeStyle.booksList}>
            {(itemsToShow || []).map(book => (
              <div key={`${book.title}-${book.album}`} className={welcomeStyle.bookItem}>
                <img src={book.img_url} alt={book.title} className={welcomeStyle.bookCover} />
                <div className={welcomeStyle.bookDetails}>
                  <h3>{book.title}</h3>
                  <p>{book.artist}</p>
                  <p>{book.album}</p>
                  <p>{book.year}</p>
                  {showingSubscriptions ? (
                    <button className={welcomeStyle.downloadButton} onClick={() => handleUnsubscribe(book.title)}>
                      Remove
                    </button>
                  ) : (
                    <button className={welcomeStyle.downloadButton} onClick={() => handleSubscribe(book)}>
                      Subscribe
                    </button>
                  )}
                </div>
              </div>
            ))}
          </div>
        </section>
      </main>
    </div>
  );
};

export default Welcome;
