import React, { useState, useEffect } from 'react';
import axios from 'axios';
import welcomeStyle from './Welcome.module.css';
import { useNavigate } from 'react-router-dom';

const Welcome = ({ user, setUserState }) => {
  const [albums, setAlbums] = useState([]);
  const [subscriptions, setSubscriptions] = useState([]);
  const [searchFields, setSearchFields] = useState({ title: '', artist: '', album: '', year: '' });
  const [loading, setLoading] = useState(true);
  const [lastKey, setLastKey] = useState(null);
  const [showingSubscriptions, setShowingSubscriptions] = useState(false);
  const navigate = useNavigate();
  const limit = 12;

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      setLoading(false);
      navigate('/login', { replace: true });
    } else {
      const storedUser = localStorage.getItem('user');
      if (storedUser) setUserState(JSON.parse(storedUser));
      setLoading(false);
      loadAlbums();
      loadSubscriptions();
    }
  }, [navigate, setUserState]);

  const loadAlbums = (lastEvaluatedKey = null) => {
    const token = localStorage.getItem('token');
    let url = `http://127.0.0.1:5000/music?limit=${limit}`;
    if (lastEvaluatedKey) {
      url += `&last_evaluated_key=${encodeURIComponent(JSON.stringify(lastEvaluatedKey))}`;
    }
    axios.get(url, { headers: { Authorization: `Bearer ${token}` } })
      .then(res => {
        const newItems = res.data.items || [];
        setAlbums(prev => lastEvaluatedKey ? [...prev, ...newItems] : newItems);
        setLastKey(res.data.lastEvaluatedKey);
      })
      .catch(err => {
        console.error(err);
        navigate('/login', { replace: true });
      });
  };

  const loadSubscriptions = () => {
    const token = localStorage.getItem('token');
    axios.get('http://127.0.0.1:5000/subscriptions', {
      headers: { Authorization: `Bearer ${token}` }
    })
    .then(res => setSubscriptions(res.data.albums || []))
    .catch(err => {
      console.error('Failed to load subscriptions:', err);
      if (err.response?.status === 401) logout();
    });
  };

  const handleSubscribe = (compositeId) => {
    const token = localStorage.getItem('token');

    if (!token) {
      console.error('❌ No token found in localStorage');
      alert('Please log in again.');
      navigate('/login', { replace: true });
      return;
    }

    if (!compositeId) {
      console.error('❌ compositeId is missing');
      alert('Invalid album selection.');
      return;
    }

    const payload = { composite_id: compositeId };
    console.log("📦 Sending to backend:", payload);

    axios.post('http://127.0.0.1:5000/subscribe', payload, {
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      }
    })
    .then(res => {
      alert(res.data.message);
      loadSubscriptions();
    })
    .catch(err => {
      console.error('🔥 Subscribe toggle failed:', err.response?.data || err);
      if (err.response?.status === 401) {
        alert('Session expired. Please log in again.');
        logout();
      } else {
        alert('Error updating subscription.');
      }
    });
  };

  const isSubscribed = (compositeId) => {
    return subscriptions.some(sub => sub.composite_id === compositeId);
  };

  const searchHandler = (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    let url = `http://127.0.0.1:5000/music/query`;
    const searchParams = new URLSearchParams();
    Object.entries(searchFields).forEach(([key, value]) => {
      if (value.trim()) searchParams.append(key, value);
    });
    if ([...searchParams].length > 0) {
      url += `?${searchParams.toString()}`;
    }
    axios.get(url, { headers: { Authorization: `Bearer ${token}` } })
      .then(res => {
        const newItems = res.data.items || res.data;
        setAlbums(newItems);
        setLastKey(null);
      })
      .catch(err => console.error(err));
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') searchHandler(e);
  };

  const loadMoreHandler = () => {
    if (lastKey) loadAlbums(lastKey);
  };

  const logout = () => {
    setUserState(null);
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/login', { replace: true });
  };

  const handleToggleView = () => {
    setShowingSubscriptions(prev => !prev);
    if (!showingSubscriptions) loadSubscriptions();
  };

  const displayedAlbums = showingSubscriptions ? subscriptions : albums;

  if (loading) return <div className={welcomeStyle.loading}>Loading...</div>;

  return (
    <div className={welcomeStyle.container}>
      <header className={welcomeStyle.topBar}>
        <div className={welcomeStyle.userName}>{user?.fname || user?.user_name} {user?.lname || ''}</div>
        <nav className={welcomeStyle.topNav}>
          <button onClick={() => setShowingSubscriptions(false)}>Albums</button>
          <button onClick={handleToggleView}>Subscriptions</button>
          <button onClick={logout}>Logout</button>
        </nav>
      </header>

      <div className={welcomeStyle.bodyContent}>
        <aside className={welcomeStyle.sidebar}>
          <form onSubmit={searchHandler} className={welcomeStyle.searchFormVertical}>
            <h3 className={welcomeStyle.searchLabel}>Search</h3>
            <input type="text" placeholder="Title" value={searchFields.title} onChange={(e) => setSearchFields({ ...searchFields, title: e.target.value })} className={welcomeStyle.searchInputVertical} />
            <input type="text" placeholder="Artist" value={searchFields.artist} onChange={(e) => setSearchFields({ ...searchFields, artist: e.target.value })} className={welcomeStyle.searchInputVertical} />
            <input type="text" placeholder="Album" value={searchFields.album} onChange={(e) => setSearchFields({ ...searchFields, album: e.target.value })} className={welcomeStyle.searchInputVertical} />
            <input type="text" placeholder="Year" value={searchFields.year} onChange={(e) => setSearchFields({ ...searchFields, year: e.target.value })} className={welcomeStyle.searchInputVertical} />
            <button type="submit" className={welcomeStyle.searchButton}>Submit</button>
          </form>
        </aside>

        <main className={welcomeStyle.mainContent}>
          <section className={welcomeStyle.booksSection}>
            <div className={welcomeStyle.booksList}>
              {(displayedAlbums || []).map(album => (
                <div key={album.composite_id} className={welcomeStyle.bookItem}>
                  <img src={album.img_url} alt={album.title} className={welcomeStyle.bookCover} />
                  <div className={welcomeStyle.bookDetails}>
                    <h3>{album.title}</h3>
                    <p>{album.artist}</p>
                    <p>{album.album}</p>
                    <p>{album.year}</p>
                    <button className={welcomeStyle.downloadButton} onClick={() => handleSubscribe(album.composite_id)}>
                      {isSubscribed(album.composite_id) ? 'Unsubscribe' : 'Subscribe'}
                    </button>
                  </div>
                </div>
              ))}
            </div>
            {lastKey && (
              <button onClick={loadMoreHandler} className={welcomeStyle.searchButton} style={{ marginTop: '20px' }}>
                Load More
              </button>
            )}
          </section>
        </main>
      </div>
    </div>
  );
};

export default Welcome;
