import React, { useState, useEffect } from 'react';
import axios from 'axios';
import welcomeStyle from './Welcome.module.css';
import { useNavigate } from 'react-router-dom';

const Welcome = ({ user, setUserState }) => {
  const [albums, setAlbums] = useState([]);
  const [subscriptions, setSubscriptions] = useState([]);
  const [searchFields, setSearchFields] = useState({ title: '', artist: '', album: '', year: '' });
  const [loading, setLoading] = useState(true);
  const [lastKey, setLastKey] = useState(null);
  const [showingSubscriptions, setShowingSubscriptions] = useState(false);
  const navigate = useNavigate();
  const limit = 12;

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      setLoading(false);
      navigate('/login', { replace: true });
    } else {
      const storedUser = localStorage.getItem('user');
      if (storedUser) setUserState(JSON.parse(storedUser));
      setLoading(false);
      loadAlbums();
      loadSubscriptions();
    }
  }, [navigate, setUserState]);

  const loadAlbums = (lastEvaluatedKey = null) => {
    const token = localStorage.getItem('token');
    let url = `http://127.0.0.1:5000/music?limit=${limit}`;
    if (lastEvaluatedKey) {
      url += `&last_evaluated_key=${encodeURIComponent(JSON.stringify(lastEvaluatedKey))}`;
    }
    axios.get(url, { headers: { Authorization: `Bearer ${token}` } })
      .then(res => {
        const newItems = res.data.items || [];
        setAlbums(prev => lastEvaluatedKey ? [...prev, ...newItems] : newItems);
        setLastKey(res.data.lastEvaluatedKey);
      })
      .catch(err => {
        console.error(err);
        navigate('/login', { replace: true });
      });
  };

  const loadSubscriptions = () => {
    const token = localStorage.getItem('token');
    axios.get('http://127.0.0.1:5000/subscriptions', {
      headers: { Authorization: `Bearer ${token}` }
    })
    .then(res => setSubscriptions(res.data.albums || []))
    .catch(err => {
      console.error('Failed to load subscriptions:', err);
      if (err.response?.status === 401) logout();
    });
  };

  const handleSubscribe = (compositeId) => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('Please log in again.');
      navigate('/login', { replace: true });
      return;
    }
    if (!compositeId) {
      alert('Invalid album selection.');
      return;
    }

    axios.post('http://127.0.0.1:5000/subscribe', { composite_id: compositeId }, {
      headers: {
        Authorization: `Bearer ${token}`,
        'Content-Type': 'application/json'
      }
    })
    .then(res => {
      alert(res.data.message);
      loadSubscriptions();
    })
    .catch(err => {
      console.error('ðŸ”¥ Subscribe error:', err.response?.data || err);
      if (err.response?.status === 401) {
        alert('Session expired. Please log in again.');
        logout();
      } else {
        alert('Error updating subscription.');
      }
    });
  };

  const isSubscribed = (compositeId) => {
    return subscriptions.some(sub => sub.composite_id === compositeId);
  };

  const searchHandler = (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    let url = `http://127.0.0.1:5000/music/query`;
    const searchParams = new URLSearchParams();
    Object.entries(searchFields).forEach(([key, value]) => {
      if (value.trim()) searchParams.append(key, value);
    });
    if ([...searchParams].length > 0) {
      url += `?${searchParams.toString()}`;
    }
    axios.get(url, { headers: { Authorization: `Bearer ${token}` } })
      .then(res => {
        const newItems = res.data.items || res.data;
        setAlbums(newItems);
        setLastKey(null);
      })
      .catch(err => console.error(err));
  };

  const logout = () => {
    setUserState(null);
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/login', { replace: true });
  };

  const handleToggleView = () => {
    setShowingSubscriptions(prev => !prev);
    if (!showingSubscriptions) loadSubscriptions();
  };

  const displayedAlbums = showingSubscriptions ? subscriptions : albums;

  if (loading) return <div className={welcomeStyle.loading}>Loading...</div>;

  return (
    <div className={welcomeStyle.container}>
      <header className={welcomeStyle.header}>
        <div className={welcomeStyle.userName}>
          {user?.fname || user?.user_name} {user?.lname || ''}
        </div>
        <div className={welcomeStyle.navButtons}>
          <button onClick={() => setShowingSubscriptions(false)}>Subscription</button>
          <button onClick={handleToggleView}>Search</button>
          <button className={welcomeStyle.logoutButton} onClick={logout}>Logout</button>
        </div>
      </header>

      <div className={welcomeStyle.bodyLayout}>
        <aside className={welcomeStyle.sidebar}>
          <h3 className={welcomeStyle.searchTitle}>Search</h3>
          <form onSubmit={searchHandler} className={welcomeStyle.searchForm}>
            <input type="text" placeholder="Title" value={searchFields.title} onChange={(e) => setSearchFields({ ...searchFields, title: e.target.value })} />
            <input type="text" placeholder="Artist" value={searchFields.artist} onChange={(e) => setSearchFields({ ...searchFields, artist: e.target.value })} />
            <input type="text" placeholder="Album" value={searchFields.album} onChange={(e) => setSearchFields({ ...searchFields, album: e.target.value })} />
            <input type="text" placeholder="Year" value={searchFields.year} onChange={(e) => setSearchFields({ ...searchFields, year: e.target.value })} />
            <button type="submit">Submit</button>
          </form>
        </aside>

        <main className={welcomeStyle.mainContent}>
          <div className={welcomeStyle.booksList}>
            {(displayedAlbums || []).map(album => (
              <div key={album.composite_id} className={welcomeStyle.bookItem}>
                <img src={album.img_url} alt={album.title} className={welcomeStyle.bookCover} />
                <div className={welcomeStyle.bookDetails}>
                  <h3>{album.title}</h3>
                  <p>{album.artist}</p>
                  <p>{album.album}</p>
                  <p>{album.year}</p>
                  <button
                    className={welcomeStyle.downloadButton}
                    onClick={() => handleSubscribe(album.composite_id)}
                  >
                    {isSubscribed(album.composite_id) ? 'Unsubscribe' : 'Subscribe'}
                  </button>
                </div>
              </div>
            ))}
          </div>
          {lastKey && (
            <button onClick={() => loadAlbums(lastKey)} className={welcomeStyle.searchButton} style={{ marginTop: '20px' }}>
              Load More
            </button>
          )}
        </main>
      </div>
    </div>
  );
};

export default Welcome;
